mapper.select:
SELECT compName, deptName, position, duty, empName, workStatus, useYn, license, emailAddr, mainDeptYn FROM empInfoManagement WHERE `query` = '@empName ("*${kwd}*");limit=100050;mode=boolean;maxmatches=100050;'
and compName = #{compName}
and workStatus = #{workStatus}

mapper.update:
update t_co_emp2 
	set login_id = #{nextLoginId}, email_addr = #{nextEmailAddr} 
		where emp_seq = (select emp_seq from (select emp_seq from t_co_emp2 where login_id = #{previousLoginId} and email_addr = #{previousEmailAddr}) a);

update t_co_emp2 
	set login_passwd = #{newPassword}, appr_passwd = #{newPassword}, pay_passwd = #{newPassword} 
		where emp_seq = (select emp_seq from (select a.emp_seq from t_co_emp2 a, t_co_emp_multi b where a.emp_seq = b.emp_seq and b.emp_name = #{empName} and a.login_id = #{loginId}) a);

choose.when:
update t_co_emp2 set login_passwd = #{loginPwd}, position_code = #{positionSeq}, duty_code = #{dutySeq}, home_tel_num = #{homePhone}, mobile_tel_num = #{phone}, zip_code = #{postNumber}, bday = #{empBirth}, join_day = #{userDate}, gender_code = #{gender}, use_yn = #{useYn}, work_status = #{workStatus},
pic_file_id = #{picPath},
modify_seq = 'admin', modify_date = now() where emp_seq = #{empSeq}

choose.otherwise:
update ${table} set dept_seq = #{deptSeq}, biz_seq = #{bizSeq}, modify_seq = 'admin', modify_date = now() where
main_dept_yn = #{mainDeptYn},
emp_seq = #{empSeq}
