SELECT a.no AS no, a.name AS name, a.age AS age, a.gender as gender, a.grade AS grade, b.name AS departments, c.name AS company, d.name AS masterGroup, a.phone AS phone FROM employees a, departments b, company c, master_group d WHERE a.departments_no = b.no AND b.company_no = c.no AND c.group_no = d.no
AND a.name = #{kwd}
