mapper.select:
select count(emp.emp_seq) from (select d.dept_name, b.position_code as positionDuty, b.duty_code as deptDuty, e.emp_name, a.login_id, a.home_tel_num, a.mobile_tel_num, a.emp_seq, b.comp_seq, b.biz_seq, b.dept_seq from t_co_emp a, t_co_emp_dept b, t_co_dept c, t_co_dept_multi d , t_co_emp_multi e where a.emp_seq = b.emp_seq and b.dept_seq = c.dept_seq and c.dept_seq = d.dept_seq and a.emp_seq = e.emp_seq and e.lang_code = #{langCode} and d.lang_code = #{langCode}) emp LEFT OUTER JOIN t_co_comp_duty_position_multi pmp ON emp.positionDuty = pmp.dp_seq AND pmp.lang_code = #{langCode} AND pmp.dp_type = 'POSITION' LEFT OUTER JOIN t_co_comp_duty_position_multi pmd ON emp.deptDuty = pmd.dp_seq AND pmd.lang_code = #{langCode} AND pmd.dp_type = 'DUTY' where
emp.comp_seq = #{seq};
emp.biz_seq = #{seq};
emp.dept_seq = #{seq};

	select 
		  count(emp.emp_seq)
	from (select
				d.dept_name,
				b.position_code as positionDuty,
				b.duty_code as deptDuty,
				e.emp_name,
				a.login_id,
				a.home_tel_num,
				a.mobile_tel_num,
				a.emp_seq,
				b.dept_seq
			  from t_co_emp a, t_co_emp_dept b, t_co_dept c, t_co_dept_multi d , t_co_emp_multi e 
			 where a.emp_seq = b.emp_seq
			   and b.dept_seq = c.dept_seq
			   and c.dept_seq = d.dept_seq 
			   and a.emp_seq = e.emp_seq
			   and e.lang_code =#{langCode}
			   and d.lang_code =#{langCode}) emp    
		LEFT OUTER JOIN t_co_comp_duty_position_multi pmp
			ON emp.positionDuty = pmp.dp_seq
			AND pmp.lang_code =#{langCode}
			AND pmp.dp_type = 'POSITION'
		LEFT OUTER JOIN t_co_comp_duty_position_multi pmd
			ON emp.deptDuty = pmd.dp_seq
			AND pmd.lang_code =#{langCode}
			AND pmd.dp_type = 'DUTY'
	  where	
  
				
  		 (emp.login_id like #{kwd}  
		 or emp.emp_name like #{kwd})
	
	
	 		emp.dept_name like #{kwd}
	
	
			pmp.dp_name like #{kwd}
	
	
			pmd.dp_name like #{kwd}
	
	
			emp.home_tel_num like #{kwd}'
	
	
			emp.mobile_tel_num like #{kwd}
	
	
	
	   (emp.login_id like #{kwd}
		  or emp.emp_name like #{kwd}
		  or emp.dept_name like #{kwd}
		  or pmp.dp_name like #{kwd}
		  or pmd.dp_name like #{kwd}
		  or emp.home_tel_num like #{kwd}
		  or emp.mobile_tel_num like #{kwd})
